    &lt;script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"&gt;&lt;/script&gt;
    &lt;script&gt;
        // Set up the scene, camera and renderer
        const scene = new THREE.Scene();
        const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
        const renderer = new THREE.WebGLRenderer({ antialias: true });
        renderer.setSize(window.innerWidth, window.innerHeight);
        document.body.appendChild(renderer.domElement);

        // Simple cube for the cart (goCart)
        const geometry = new THREE.BoxGeometry(1, 1, 0.5);
        const material = new THREE.MeshBasicMaterial({ color: 0x00ff00 });
        const goCart = new THREE.Mesh(geometry, material);
        scene.add(goCart);

        // Position the camera so we can see the cart
        camera.position.z = 5;

        // Animation loop to render the scene continuously
        function animate() {
            requestAnimationFrame(animate);
            goCart.rotation.y += 0.01; // Rotate the cart for demonstration purposes.
            renderer.render(scene, camera);
        }
        animate();
    &lt;/script&gt;
