<html lang="en">
<head>
<title>Rotating Hexagon with Bouncing Balls</title>
<style>
body {
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
}
h1 {
  margin-bottom: 24px;
}
#hexagon {
  position: relative;
  width: 300px;
  height: 300px;
}
.ball {
  position: absolute;
  width: 24px;
  height: 24px;
  background-color: black;
  border-radius: 50%;
  box-shadow: 0 4px 6px rgba(0,0,0,0.1);
}
</style>
</head>
<body onload="setup()">
<h1>Rotating Hexagon with Bouncing Balls</h1>
<div id="hexagon"></div>
<script>
let canvas = document.getElementById('hexagon');
canvas.style.transformOrigin = 'center';

let angle = 0;
function rotateHexagon() {
    angle += 5; // Rotate the hexagon by 5 degrees each frame
    canvas.style.transform = `rotate(${angle}deg) translate(150px, 150px)`;
}
setInterval(rotateHexagon, 16); // Approximately 60 frames per second

let balls = [];
function setup() {
    for (let i = 0; i < 3; i++) {
        let ball = document.createElement('div');
        ball.className = 'ball';
        canvas.appendChild(ball);
        // Initialize the position of each ball randomly within the hexagon
        let radius = Math.random() * 100 + 25; // Random radius between 25 and 125 pixels
        balls.push({
            x: 150 + Math.cos(angle) * radius,
            y: 150 + Math.sin(angle) * radius,
            vx: (Math.random() - 0.5) * 4, // Random velocity between -2 and 2 pixels per frame
            vy: (Math.random() - 0.5) * 4, // Random velocity between -2 and 2 pixels per frame
        });
    }
}
function update() {
    for (let ball of balls) {
        // Update the position of each ball
        ball.x += ball.vx;
        ball.y += ball.vy;
        // Check for collisions with the edges of the hexagon and bounce back if necessary
        if (ball.x <= 50 || ball.x >= 250 || ball.y <= 50 || ball.y >= 250) {
            ball.vx = -ball.vx; // Reverse the x velocity when bouncing from a vertical edge
            ball.vy = -ball.vy; // Reverse both directions when bouncing from a horizontal edge
        }
    }
}
setInterval(update, 16); // Approximately 60 frames per second
</script>
</body>
</html>"}
