<head>
<meta charset='UTF-8'/><meta name="viewport" content="width=device-width, initial-scale=1.0"><title>Rotating Hexagon with Bouncing Balls</title>
<style>
  svg {
    display: block;
    margin-left: auto;
    margin-right: auto;
    background-color: black;
  }

  .hexagon {
    transform-origin: center;
    animation: rotateHexagon 2s infinite linear;
  }

  @keyframes rotateHexagon {
    from {transform: rotate(0deg);}
    to {transform: rotate(360deg);}
  }
</style>
<script type='text/javascript'>
function getRandomInt(max) {
  return Math.floor(Math.random() * max);
}

var balls = [];
var canvasWidth = 800, canvasHeight = 600; // Define the width and height of your SVG

function createBall(x, y, radius) {
    var ball = {x: x, y: y, vx: (getRandomInt(5) - 2), vy: (getRandomInt(5) - 2), radius: radius};
    balls.push(ball);
}

function draw() {
    document.getElementById('myCanvas').innerHTML = ''; // Clear the canvas each frame
    var canvas = document.createElementNS('http://www.w3.org/2000/svg', 'svg');
    canvas.setAttribute('width', 800);
    canvas.setAttribute('height', 600);
    for (var i = 0; i < balls.length; i++) {
        var circle = document.createElementNS('http://www.w3.org/2000/svg', 'circle');
        circle.setAttribute('cx', balls[i].x - balls[i].radius);
        circle.setAttribute('cy', balls[i].y - balls[i].radius);
        circle.setAttribute('r', balls[i].radius);
        circle.setAttribute('fill', 'white');
        canvas.appendChild(circle);
    }
    document.body.appendChild(canvas);
}

window.onload = function() {
  for (var i = 0; i < 25; i++) { // Create 25 bouncing balls
      createBall(getRandomInt(800), getRandomInt(600), Math.floor(Math.random() * 10) + 3); // Initial radius between 3 and 12
  }
