{"filename":"index.html","content":"<!DOCTYPE html>
<html>
<head>
    <title>Go-Kart Racing Game</title>
    <style>
        body { margin: 0; }
        canvas { display: block; }
    </style>
</head>
<body>
<script src="https://cdn.jsdelivr.net/npm/three@0.154.0/build/three.min.js"></script>
<script>
// Three.js game code here
let scene = new THREE.Scene();
let camera = new THREE.PerspectiveCamera(75, window.innerWidth/window.innerHeight, 0.1, 1000);
camera.position.set(0, 10, 20);

let renderer = new THREE.WebGLRenderer();
renderer.setSize(window.innerWidth, window.innerHeight);
document.body.appendChild(renderer.domElement);

let light = new THREE.DirectionalLight(0xffffff, 1);
light.position.set(5, 10, 7.5);
scene.add(light);

// Create road
let roadGeometry = new THREE.PlaneGeometry(20, 20);
let roadMaterial = new THREE.MeshStandardMaterial({ color: 0x444444 });
let road = new THREE.Mesh(roadGeometry, roadMaterial);
road.rotation.x = -Math.PI/2;
scene.add(road);

// Create car
let carGeometry = new THREE.BoxGeometry(1, 0.5, 2);
let carMaterial = new THREE.MeshStandardMaterial({ color: 0xff0000 });
let car = new THREE.Mesh(carGeometry, carMaterial);
car.position.y = 0.25;
scene.add(car);

// Controls
let keys = {};
window.addEventListener('keydown', (e) => keys[e.key] = true);
window.addEventListener('keyup', (e) => keys[e.key] = false);

function animate() {
    requestAnimationFrame(animate);

    // Movement logic
    let speed = 0.1;
    if (keys['w']) car.position.z -= speed;
    if (keys['s']) car.position.z += speed;
    if (keys['a']) car.position.x -= speed;
    if (keys['d']) car.position.x += speed;

    renderer.render(scene, camera);
}
animate();
</script>
</body>
</html>"}
