{"content":"<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <title>Rotating Hexagon with Bouncing Balls</title>
    <style>
        body {
            margin: 0;
            overflow: hidden;
            background: #000;
        }
        .hexagon {
            position: absolute;
            top: 50%;
            left: 50%;
            width: 200px;
            height: 200px;
            transform: translate(-50%, -50%) rotate(0deg);
            animation: rotate 10s linear infinite;
            clip-path: polygon(50% 0%, 100% 25%, 100% 75%, 50% 100%, 0% 75%, 0% 25%);
        }
        .ball {
            position: absolute;
            border-radius: 50%;
            background: #fff;
            width: 10px;
            height: 10px;
        }
        @keyframes rotate {
            from { transform: translate(-50%, -50%) rotate(0deg); }
            to { transform: translate(-50%, -50%) rotate(360deg); }
        }
    </style>
</head>
<body>
    <div class="hexagon" id="hexContainer"></div>
    <script>
        const balls = [];
        const numBalls = 10;
        const radius = 100;

        for (let i = 0; i < numBalls; i++) {
            const ball = document.createElement('div');
            ball.classList.add('ball');
            document.getElementById('hexContainer').appendChild(ball);
            let angle = Math.random() * 2 * Math.PI;
            let dist = Math.random() * radius;
            const cx = 100;
            const cy = 100;
            ball.style.left = (cx + Math.cos(angle) * dist - 5) + 'px';
            ball.style.top = (cy + Math.sin(angle) * dist - 5) + 'px';
            balls.push({
                el: ball,
                x: cx + Math.cos(angle) * dist,
                y: cy + Math.sin(angle) * dist,
                dx: (Math.random() - 0.5) * 2,
                dy: (Math.random() - 0.5) * 2
            });
        }

        function animate() {
            const cx = 100;
            const cy = 100;
            for (let ball of balls) {
                ball.x += ball.dx;
                ball.y += ball.dy;

                // Check if outside the circle
                const dx = ball.x - cx;
                const dy = ball.y - cy;
                const distSq = dx*dx + dy*dy;
                if (distSq > radius*radius) {
                    ball.dx *= -1;
                    ball.dy *= -1;
                }

                ball.el.style.left = (ball.x - 5) + 'px';
                ball.el.style.top = (ball.y - 5) + 'px';
            }
            requestAnimationFrame(animate);
        }

        animate();
    </script>
</body>
</html>","file_name":"index.html"}
