<html>
<head>
    <style>
        @keyframes rotate {
            from { transform: rotate(0deg); }
            to { transform: rotate(360deg); }
        }
        .hexagon {
            animation: rotate 5s linear infinite;
        }
        @keyframes bounce {
            0%, 100% { transform: translateY(0); }
            50% { transform: translateY(-20px); }
        }
        .ball {
            animation: bounce 1s ease-in-out infinite;
        }
    </style>
</head>
