{"filename":"index.html","content":"<!DOCTYPE html>
<html>
<head>
    <style>
        body {
            margin: 0;
            overflow: hidden;
            background: #111;
        }
        .hexagon {
            width: 300px;
            height: 300px;
            position: absolute;
            top: 50%;
            left: 50%;
            transform: translate(-50%, -50%);
            background: #222;
            clip-path: polygon(50% 0%, 100% 25%, 100% 75%, 50% 100%, 0% 75%, 0% 25%);
            animation: rotate 10s linear infinite;
        }
        .ball {
            position: absolute;
            width: 10px;
            height: 10px;
            background: #0f0;
            border-radius: 50%;
            transform: translate(-50%, -50%);
        }
        @keyframes rotate {
            from { transform: translate(-50%, -50%) rotate(0deg); }
            to { transform: translate(-50%, -50%) rotate(360deg); }
        }
    </style>
</head>
<body>
    <div class="hexagon" id="hexagon">
        <!-- Balls will be added here -->
    </div>
    <script>
        const hexagon = document.getElementById('hexagon');
        let balls = [];
        const numBalls = 20;

        for (let i = 0; i < numBalls; i++) {
            const ball = document.createElement('div');
            ball.className = 'ball';
            let x = Math.random() * hexagon.clientWidth;
            let y = Math.random() * hexagon.clientHeight;
            ball.style.left = x + 'px';
            ball.style.top = y + 'px';
            balls.push({ el: ball, x, y, dx: (Math.random() - 0.5) * 2, dy: (Math.random() - 0.5) * 2 });
            hexagon.appendChild(ball);
        }

        function animate() {
            const rect = hexagon.getBoundingClientRect();
            for (let ball of balls) {
                ball.x += ball.dx;
                ball.y += ball.dy;

                // Bounce off edges
                if (ball.x < 0 || ball.x > rect.width - 10) ball.dx *= -1;
                if (ball.y < 0 || ball.y > rect.height - 10) ball.dy *= -1;

                ball.el.style.left = ball.x + 'px';
                ball.el.style.top = ball.y + 'px';
            }
            requestAnimationFrame(animate);
        }

        animate();
    </script>
</body>
</html>"}
