{"content": "<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Rotating Hexagon with Bouncing Balls</title>
  <style>
    body {
      background-color: #222;
      margin: 0;
      padding: 0;
      display: flex;
      justify-content: center;
      align-items: center;
      height: 100vh;
      overflow: hidden;
    }
    .hexagon {
      position: relative;
      width: 300px;
      height: 260px;
      background-color: #444;
      clip-path: polygon(25% 5%, 75% 5%, 100% 50%, 75% 95%, 25% 95%, 0% 50%);
      animation: rotateHexagon 10s linear infinite;
      overflow: hidden;
    }

    /* Bouncing ball style */
    .ball {
      width: 20px;
      height: 20px;
      border-radius: 50%;
      background-color: #ff4757;
      position: absolute;
      animation: bounce 2s ease-in-out infinite;
    }

    /* Hexagon rotation animation */
    @keyframes rotateHexagon {
      from { transform: rotate(0deg); }
      to { transform: rotate(360deg); }
    }

    /* Bouncing animation */
    @keyframes bounce {
      0%, 100% {
        transform: translate(0, 0);
      }
      25% {
        transform: translate(30px, -50px);
      }
      50% {
        transform: translate(0, -100px);
      }
      75% {
        transform: translate(-30px, -50px);
      }
    }
  </style>
</head>
<body>
  <div class="hexagon">
    <div class="ball" style="left: 25%; top: 70%; animation-delay: 0s;"></div>
    <div class="ball" style="left: 50%; top: 70%; animation-delay: 0.5s;"></div>
    <div class="ball" style="left: 75%; top: 70%; animation-delay: 1s;"></div>
  </div>
</body>
</html>
