{"content": "let currentPlayer = 'X';
let gameBoard = ['', '', '', '', '', '', '', '', '];
const winningConditions = [
    [0, 1, 2],
    [3, 4, 5],
    [6, 7, 8],
    [0, 3, 6],
    [1, 4, 7],
    [2, 5, 8],
    [0, 4, 8],
    [2, 4, 6]
];

const startGame = () => {
    document.querySelectorAll('.cell').forEach(cell => cell.addEventListener('click', handleCellClick));
};

const handleCellClick = (e) => {
    const clickedCell = e.target;
    const clickedCellIndex = parseInt(clickedCell.getAttribute('data-cell-index'), 10);

    if(gameBoard[clickedCellIndex] === '' && !checkWin(currentPlayer)){
        makeMove(clickedCell, clickedCellIndex, currentPlayer);
        gameBoard[clickedCellIndex] = currentPlayer;
        if(checkWin(currentPlayer)){
            alert(`${currentPlayer} wins!`);
            resetGame();
        } else if(gameBoard.every(cell => cell !== '')) {
            alert('It's a draw!');
            resetGame();
        } else {
            currentPlayer = currentPlayer === 'X' ? 'O' : 'X';
        }
    }
};

const makeMove = (cell, index, player) => {
    cell.textContent = player;
    cell.classList.add(player);
};

const checkWin = (player) => {
    return winningConditions.some(condition => {
        return condition.every(index => {
            return gameBoard[index] === player;
        });
    });
};

const resetGame = () => {
    document.querySelectorAll('.cell').forEach(cell => {
        cell.textContent = '';
        cell.classList.remove('X', 'O');
    });
    gameBoard.fill('');
    currentPlayer = 'X';
    startGame();
};

startGame();", "file_name": "script.js"}<|assistant|>create_file
